// src/pages/About.js
import React from 'react';

const About = () => {
  return (
    <section className="bg-white min-h-[80vh] py-16 px-4">
      <div className="max-w-5xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-gray-800 mb-6">About Us</h1>
        <p className="text-gray-600 text-lg mb-8 leading-relaxed">
          Coding Ninjas is India’s leading tech education platform. We aim to bridge the gap between
          academic knowledge and industry skills by providing world-class coding courses, live mentorship, and
          placement assistance.
        </p>
        <p className="text-gray-600 text-lg mb-4 leading-relaxed">
          We’ve helped over <span className="text-orange-500 font-semibold">100,000+</span> students land their
          dream jobs at top tech companies like Google, Microsoft, Flipkart, and more.
        </p>
        <p className="text-gray-600 text-lg leading-relaxed">
          Our mission is simple: <span className="italic text-orange-600">to make tech education accessible, affordable, and outcome-driven.</span>
        </p>
      </div>
    </section>
  );
};

export default About;
