import React from 'react';
import { Link } from 'react-router-dom';

const CourseCard = ({ title, desc, image, price }) => {
  return (
    <div className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition-all duration-300">
      <img src={image} alt={title} className="w-full h-40 object-cover rounded" />
      <h3 className="mt-4 text-lg font-semibold text-gray-800">{title}</h3>
      <p className="text-sm text-gray-600 mt-2">{desc}</p>
      <p className="text-md text-orange-600 font-semibold mt-2">Price: {price}</p>

      <Link
        to={`/courses/${title.toLowerCase().includes('full') ? 'fullstack' :
          title.toLowerCase().includes('data') ? 'datasci' :
          title.toLowerCase().includes('competitive') ? 'cp' :
          title.toLowerCase().includes('android') ? 'android' : 'unknown'
        }`}
        className="mt-4 inline-block bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded"
      >
        Know More
      </Link>
    </div>
  );
};

export default CourseCard;
