import React from 'react';
import { Link } from 'react-router-dom';

const CourseCard = ({ title, desc, image, price }) => {
  return (
    <div className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition-all duration-300">
      <img
        src={image}
        alt={title}
        className="w-full h-48 object-cover rounded-md"
      />
      <h3 className="mt-4 text-lg font-semibold text-gray-800">{title}</h3>
      <p className="text-sm text-gray-600 mt-2">{desc}</p>
      <p className="text-md font-semibold text-orange-600 mt-2">{price}</p>
      <Link
        to={`/courses/${title.toLowerCase().replace(/\s+/g, '-')}`}
        className="mt-4 inline-block bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded"
      >
        Know More
      </Link>
    </div>
  );
};

export default CourseCard;
