import React from 'react';
import { Link } from 'react-router-dom';

function CourseCard({ title, desc, image, price }) {
  return (
    <div className="bg-white rounded shadow p-3 hover:shadow-md transition text-sm">
      {/* Course Image */}
      <img
        src={image}
        alt={title}
        className="w-full h-36 object-cover rounded"
      />

      {/* Title */}
      <h3 className="mt-3 text-lg font-semibold text-gray-800">{title}</h3>

      {/* Description */}
      <p className="mt-1 text-gray-600">{desc}</p>

      {/* Price */}
      <p className="mt-1 text-orange-600 font-bold">{price}</p>

      {/* Know More Button */}
      <Link
        to={`/courses/${title.toLowerCase().replace(/\s+/g, '-')}`}
        className="inline-block mt-3 bg-orange-500 hover:bg-orange-600 text-white px-3 py-1.5 rounded text-sm"
      >
        Know More
      </Link>
    </div>
  );
}

export default CourseCard;
