import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [openDropdown, setOpenDropdown] = useState(null);

  const toggleDropdown = (menu) => {
    setOpenDropdown(openDropdown === menu ? null : menu);
  };

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        <Link to="/" className="text-xl font-bold text-orange-600">
          <img src="./i" alt="Logo" className="h-8" />
        </Link>

        <div className="flex space-x-6 items-center">
          {/* Bootcamp Dropdown */}
          <div
            className="relative"
            onMouseLeave={() => setOpenDropdown(null)}
          >
            <button
              onClick={() => toggleDropdown('bootcamp')}
              className="hover:text-orange-500 font-medium"
            >
              Job Bootcamp ▾
            </button>
            {openDropdown === 'bootcamp' && (
              <div className="absolute top-10 left-0 bg-white border shadow p-2 rounded w-48 z-50">
                <Link to="#" className="block px-4 py-2 hover:bg-gray-100">Full Stack</Link>
                <Link to="#" className="block px-4 py-2 hover:bg-gray-100">Data Science</Link>
              </div>
            )}
          </div>

          {/* Courses Link */}
          <Link to="/courses" className="hover:text-orange-500 font-medium">Courses</Link>

          {/* Certification Dropdown */}
          <div
            className="relative"
            onMouseLeave={() => setOpenDropdown(null)}
          >
            <button
              onClick={() => toggleDropdown('certification')}
              className="hover:text-orange-500 font-medium"
            >
              IIT Certifications ▾
            </button>
            {openDropdown === 'certification' && (
              <div className="absolute top-10 left-0 bg-white border shadow p-2 rounded w-48 z-50">
                <Link to="#" className="block px-4 py-2 hover:bg-gray-100">IIT Madras</Link>
                <Link to="#" className="block px-4 py-2 hover:bg-gray-100">IIT Roorkee</Link>
              </div>
            )}
          </div>

          {/* Login */}
          <Link
            to="/login"
            className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded font-medium"
          >
            Login
          </Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
