import React from 'react';

const Home = () => {
  return (
    <section className="bg-gray-50 min-h flex flex-col justify-center items-center text-center px-4">
      <h1 className="text-4xl md:text-5xl font-bold text-gray-900 leading-tight">
        Restricted by opportunities?
        <br />
        <span className="text-orange-500">Get the tech career you deserve. Faster.</span>
      </h1>
      <p className="mt-4 text-lg md:text-xl text-gray-600 max-w-2xl">
        Coding Ninjas is a platform to learn coding from industry experts and kickstart your career.
      </p>
      <button className="mt-6 px-6 py-3 bg-orange-500 text-white rounded-lg text-lg hover:bg-orange-600 transition">
        Explore Offerings
      </button>
    </section>
  );
};

export default Home;
