import React from 'react';

const plans = [
  {
    name: 'Basic',
    price: '₹4,999',
    features: ['Access to beginner courses', 'Email support', 'Certificate on completion'],
    highlight: false,
  },
  {
    name: 'Pro',
    price: '₹9,999',
    features: ['All Basic features', 'Live mentor sessions', 'Project feedback', 'Priority support'],
    highlight: true,
  },
  {
    name: 'Ultimate',
    price: '₹14,999',
    features: ['All Pro features', '1:1 Career Guidance', 'Resume Building', 'Mock Interviews'],
    highlight: false,
  },
];

const Pricing = () => {
  return (
    <section className="bg-gray-100 py-16 px-4">
      <div className="max-w-7xl mx-auto text-center">
        <h2 className="text-3xl md:text-4xl font-bold text-gray-800 mb-12">
          Choose Your Learning Plan
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {plans.map((plan, idx) => (
            <div
              key={idx}
              className={`rounded-lg shadow-lg p-6 bg-white border-2 ${
                plan.highlight ? 'border-orange-500' : 'border-transparent'
              }`}
            >
              <h3 className="text-xl font-semibold text-gray-800 mb-2">{plan.name}</h3>
              <p className="text-3xl font-bold text-orange-500 mb-4">{plan.price}</p>
              <ul className="text-gray-600 mb-6 space-y-2 text-sm">
                {plan.features.map((feature, i) => (
                  <li key={i}>• {feature}</li>
                ))}
              </ul>
              <button
                className={`w-full py-2 px-4 rounded ${
                  plan.highlight
                    ? 'bg-orange-500 hover:bg-orange-600 text-white'
                    : 'bg-gray-300 hover:bg-gray-400 text-gray-800'
                }`}
              >
                Get Started
              </button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Pricing;
