import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [openDropdown, setOpenDropdown] = useState(null);

  const toggleDropdown = (menu) => {
    setOpenDropdown(openDropdown === menu ? null : menu);
  };

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50">

      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">

        {/* Logo */}
        <Link to="/" className="text-xl font-bold text-orange-600">
          <img
            src="https://ninjasfiles.s3.amazonaws.com/0000000000000723.jpg"
            alt="Logo"
            className="h-8"
          />
        </Link>



        {/* Links */}
        <div className="flex space-x-6 items-center">
          {/* ✅ Home Link */}
  <Link to="/" className="hover:text-orange-500 font-medium">Home</Link>
          {/* ✅ Wrap in a relative block for click-based dropdown */}
          <div className="relative">
            <button
              onClick={() => toggleDropdown('bootcamp')}
              className="hover:text-orange-500 font-medium"
            >
              Job Bootcamp ▾
            </button>
            {openDropdown === 'bootcamp' && (
              <div
                className="absolute top-10 left-0 bg-white border shadow p-2 rounded w-48 z-50"
              >
                <Link
                  to="/courses/fullstack"
                  className="block px-4 py-2 hover:bg-gray-100"
                  onClick={() => setOpenDropdown(null)} // ✅ close dropdown after click
                >
                  Full Stack
                </Link>
                <Link
                  to="/courses/datasci"
                  className="block px-4 py-2 hover:bg-gray-100"
                  onClick={() => setOpenDropdown(null)}
                >
                  Data Science
                </Link>
              </div>
            )}
          </div>

          <Link to="/courses" className="hover:text-orange-500 font-medium">Courses</Link>

          <div className="relative">
            <button
              onClick={() => toggleDropdown('certification')}
              className="hover:text-orange-500 font-medium"
            >
              IIT Certifications ▾
            </button>
            {openDropdown === 'certification' && (
              <div className="absolute top-10 left-0 bg-white border shadow p-2 rounded w-48 z-50">
                <Link to="https://www.iitm.ac.in/" className="block px-4 py-2 hover:bg-gray-100">IIT Madras</Link>
                <Link to="https://www.iitr.ac.in/" className="block px-4 py-2 hover:bg-gray-100">IIT Roorkee</Link>
              </div>
            )}
          </div>

          <Link
            to="/login"
            className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded font-medium"
          >
            Login
          </Link>
        </div>

      </div>
    </nav>
  );
};

export default Navbar;
