import React from 'react';
import { useParams } from 'react-router-dom';

const courseData = {
  fullstack: {
    title: 'Full Stack Web Development',
    desc: 'Learn to build complete web apps using MERN (MongoDB, Express, React, Node.js).',
    image: 'https://ninjasfiles.s3.amazonaws.com/full-stack.png'
  },
  datasci: {
    title: 'Data Science & Machine Learning',
    desc: 'Master AI tools, Python, Pandas, NumPy, and ML concepts hands-on.',
    image: 'https://ninjasfiles.s3.amazonaws.com/data-science.png'
  },
  cp: {
    title: 'Competitive Programming',
    desc: 'Sharpen your problem-solving skills for contests and interviews.',
    image: 'https://ninjasfiles.s3.amazonaws.com/cp-course.png'
  },
  android: {
    title: 'Android Development',
    desc: 'Create stunning Android apps with Kotlin and Jetpack libraries.',
    image: 'https://ninjasfiles.s3.amazonaws.com/android-course.png'
  }
};

const CourseDetail = () => {
  const { id } = useParams();
  const course = courseData[id];

  if (!course) {
    return <div className="p-6 text-center text-red-500">Course not found!</div>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <img src={course.image} alt={course.title} className="rounded mb-6 w-full h-60 object-cover" />
      <h1 className="text-3xl font-bold mb-2">{course.title}</h1>
      <p className="text-gray-700 text-lg">{course.desc}</p>
    </div>
  );
};

export default CourseDetail;
