import React from 'react';
import CourseCard from './components/ourseCard';

const courses = [
  {
    title: 'Full Stack Web Development',
    desc: 'Learn to build complete web applications using MERN stack.',
    image: 'https://ninjasfiles.s3.amazonaws.com/full-stack.png'
  },
  {
    title: 'Data Science & Machine Learning',
    desc: 'Master ML, data analysis and AI tools from scratch.',
    image: 'https://ninjasfiles.s3.amazonaws.com/data-science.png'
  },
  {
    title: 'Competitive Programming',
    desc: 'Ace coding interviews and coding competitions.',
    image: 'https://ninjasfiles.s3.amazonaws.com/cp-course.png'
  },
  {
    title: 'Android Development',
    desc: 'Build beautiful mobile apps using Kotlin and Jetpack.',
    image: 'https://ninjasfiles.s3.amazonaws.com/android-course.png'
  }
];

const Courses = () => {
  return (
    <div className="bg-gray-100 py-10 px-6">
      <h2 className="text-3xl font-bold text-center mb-10 text-gray-800">
        Explore Our Courses
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
        {courses.map((course, idx) => (
          <CourseCard key={idx} {...course} />
        ))}
      </div>
    </div>
  );
};

export default Courses;
