import React from 'react';

const CourseCard = ({ title, desc, image }) => {
  return (
    <div className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition-all duration-300">
      <img src={image} alt={title} className="w-full h-40 object-cover rounded" />
      <h3 className="mt-4 text-lg font-semibold text-gray-800">{title}</h3>
      <p className="text-sm text-gray-600 mt-2">{desc}</p>
      <button className="mt-4 bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded">
        Know More
      </button>
    </div>
  );
};

export default CourseCard;
