import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const CourseCard = ({ title, desc, image, price, slug }) => {
  return (
    <motion.div
      className="bg-white rounded shadow p-2 hover:shadow-sm transition text-xs"
      whileHover={{ scale: 1.03 }}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <img src={image} alt={title} className="w-full h-28 object-cover rounded" />
      <h3 className="mt-2 text-sm font-semibold text-gray-800">{title}</h3>
      <p className="mt-1 text-gray-600 leading-snug">{desc}</p>
      <p className="mt-1 text-orange-600 font-bold">{price}</p>
      <Link
        to={`/courses/${slug}`}
        className="inline-block mt-2 bg-orange-500 hover:bg-orange-600 text-white px-2 py-1 rounded text-xs"
      >
        Know More
      </Link>
    </motion.div>
  );
};

export default CourseCard;
